package main.scala.Day2

import main.scala.TestBase

class Tests extends TestBase {
  test("Example 1") {
    val runner = new Runner()
    runner.run(
      """ULL
        |RRDDD
        |LURDL
        |UUUUD""".stripMargin)
    assert(runner.code() == "1985")
  }

  test("Puzzle 1") {
    val runner = new Runner()
    runner.run(
      """
        |
        |
        |
        |""".stripMargin)
    println(runner.code())
  }

  test("Example 2.1") {
    val runner = new Runner2()
    runner.run(
      """ULL
        |RRDDD
        |LURDL
        |UUUUD""".stripMargin)
    assert(runner.code() == "5DB3")
  }

  test("Puzzle 2") {
    val runner = new Runner2()
    runner.run(
      """
        |
        |
        |
        |""".stripMargin)
    println(runner.code())
  }
}
